property.code:
if(require.toUrl('xwiki-meta').substr(0,7) === '/xwiki/') {
  var required = ['jquery', 'xwiki-meta'];
}
else {
  var required = ['jquery'];
}
require(required , function ($, xm) {
$(function(){
  var insertTags = function(){return true}; // Prevent issues with Syntax Highligthing extnesion
  var visibleSpaces = getCookieHierarchy('visibleSpaces').split(',');
  var visiblePages = getCookieHierarchy('visiblePages').split(',');
  var visibleCategory = getCookieHierarchy('visibleCategory').split(',');
  if(XWiki.webapppath.substr(0,1) === '/') {
    XWiki.webapppath = XWiki.webapppath.substr(1,(XWiki.webapppath.length-1));
  }
  var wikiGetURL = '/'+XWiki.webapppath+''+XWiki.servletpath+'/get/';
  if(window.docgeturl.indexOf('/get/') >= 0) {
    wikiGetURL = window.docgeturl.substring(0,window.docgeturl.indexOf('/get/')+5);
  }
  var currentEditor = 'view';
  var currentPage = encodeURIComponent(XWiki.currentPage);
  var currentSpace = encodeURIComponent(XWiki.currentSpace);
  var currentTree = getCookieHierarchy('currentTree') || 'logical';
  var hashURL = window.location.hash.substring(1).split('&!!');
  if(hashURL.size() > 3 && hashURL[2] == 'physical') {
    currentTree = 'physical';
  }
  if(hashURL.size() >= 5) {
    currentPage = hashURL[3];
    currentSpace = hashURL[4];
  }
  function loadHierarchy() {
    $.ajax({
      url: wikiGetURL+"WebIDECode/ApplicationHierarchy",
      type: "GET",
      datatype : "html",
      data : "action=view&editor="+currentEditor+"&masterPage="+XWiki.currentSpace+"."+XWiki.currentPage+"&remotePage="+currentSpace+"."+currentPage+"&visibleSpaces="+encodeURIComponent(visibleSpaces)+"&visiblePages="+encodeURIComponent(visiblePages)+"&visibleCategory="+encodeURIComponent(visibleCategory)+"&treeview="+currentTree,
      contentType: "text/html",
    }).success(function(data){
      $('#hierarchyBlock').html(data);
      $('head').append('<script type="text/javascript" src="/'+XWiki.webapppath+''+XWiki.servletpath+'jsx/WebIDECode/ApplicationHierarchy" defer="defer"></script>');
    });
  }
  function getCookieHierarchy(cname) {
    var name = XWiki.currentPage+"-"+cname+"=";
    var ca = document.cookie.split(';');
    for(var i=0; i<ca.length; i++) {
      var c = ca[i];
      while (c.charAt(0)==' ') c = c.substring(1);
      if (c.indexOf(name) == 0) return c.substring(name.length,c.length);
    }
    return "";
  }
  loadHierarchy();

});
});

